function deliverOrder (sprite: Sprite) {
    sprites.setDataBoolean(sprite, "delivered", true)
    if (sprites.readDataNumber(sprite, "order") == currentOrders[0]) {
        timeBar.value = 50
        music.play(music.melodyPlayable(music.baDing), music.PlaybackMode.InBackground)
        currentDeliveries += 1
        earning = calculateEarning()
        currentMoney += earning
        earningIndicator = textsprite.create("+" + earning + "$", 0, 7)
        earningIndicator.setOutline(1, 6)
        earningIndicator.setPosition(sprite.x, sprite.y)
        earningIndicator.vx = 0 - speed
        earningIndicator.vy = -15
        earningIndicator.lifespan = 750
        earningIndicator.z = 10
        updateEarningDisplay()
    } else {
        music.play(music.melodyPlayable(music.smallCrash), music.PlaybackMode.InBackground)
        misses += 1
        missMarkers.setImage(missMarkerStates[misses])
        if (misses == 3) {
            gameOver()
        }
    }
}
