function calculateEarning () {
    return Math.round(2 + (speed - 60) / 140 * 10 + randint(0, 5))
}
function buySkin (index: number) {
    if (listIncludes(ownedSkins, index)) {
        currentSkin = index
        blockSettings.writeNumber("currentSkin", currentSkin)
        updateSkin(index)
    } else if (highscore < skinPrices[index][1]) {
        purchaseFailText = textsprite.create(blockObject.getStringProperty(language, StrProp.skinLockedMessage), 12, 11)
        purchaseFailText.setPosition(80, skinDistanceText.y)
        purchaseFailText.lifespan = 1000
        scene.cameraShake(2, 500)
    } else if (money < skinPrices[index][0]) {
        purchaseFailText = textsprite.create(blockObject.getStringProperty(language, StrProp.notEnoughMoneyMessage), 12, 11)
        purchaseFailText.setPosition(80, skinDistanceText.y)
        purchaseFailText.lifespan = 1000
        scene.cameraShake(2, 500)
    } else {
        money += 0 - skinPrices[index][0]
        blockSettings.writeNumber("money", money)
        currentSkin = index
        blockSettings.writeNumber("currentSkin", currentSkin)
        ownedSkins.push(index)
        blockSettings.writeNumberArray("ownedSkins", ownedSkins)
        updateSkin(index)
    }
}
