function spawnHouse () {
    while (houseOrder == lastOrder) {
        houseOrder = randint(0, 5)
    }
    lastOrder = houseOrder
    houseSprites.push(sprites.create(houses._pickRandom(), SpriteKind.House))
    houseOrderSprites.push(sprites.create(foodItems[houseOrder][0], SpriteKind.Food))
    newHouse = houseSprites[houseSprites.length - 1]
    newHouseOrder = houseOrderSprites[houseOrderSprites.length - 1]
    newHouse.bottom = 66
    newHouse.left = 160
    newHouse.vx = 0 - speed
    newHouse.setFlag(SpriteFlag.AutoDestroy, true)
    sprites.setDataNumber(newHouse, "order", houseOrder)
    sprites.setDataBoolean(newHouse, "delivered", false)
    newHouseOrder.x = newHouse.x
    newHouseOrder.bottom = newHouse.top + 3
    newHouseOrder.vx = 0 - speed
}
