function listIncludes (list: any[], number: number) {
    includes = false
    for (let value of list) {
        if (number == value) {
            includes = true
            break;
        }
    }
    return includes
}
function updateSkin (index: number) {
    sprites.destroy(skinStatusText)
    van.setImage(skins[index][0])
    skinDistanceText.setText("" + blockObject.getStringProperty(language, StrProp.shopRequiredDisplay) + ": " + convertToKM(skinPrices[index][1]))
    skinCostText.setText("" + blockObject.getStringProperty(language, StrProp.shopCostDisplay) + ": " + convertToKilos(skinPrices[index][0]) + "$")
    skinIndexText.setText("(" + ("" + (index + 1) + "/" + skins.length) + ")")
    shopMoneyDisplay.setText("" + money + "$")
    if (listIncludes(ownedSkins, index)) {
        if (currentSkin == index) {
            skinStatusText = textsprite.create(blockObject.getStringProperty(language, StrProp.shopSelectedIndicator), 0, 6)
            skinStatusText.setOutline(1, 8)
        } else {
            skinStatusText = textsprite.create(blockObject.getStringProperty(language, StrProp.shopOwnedIndicator), 0, 7)
            skinStatusText.setOutline(1, 6)
        }
    } else if (highscore >= skinPrices[index][1]) {
        skinStatusText = textsprite.create(blockObject.getStringProperty(language, StrProp.shopBuyIndicator), 0, 5)
        skinStatusText.setOutline(1, 4)
    } else {
        skinStatusText = textsprite.create(blockObject.getStringProperty(language, StrProp.shopLockedIndicator), 0, 2)
        skinStatusText.setOutline(1, 14)
        van.setImage(lockedSkin)
        skinCostText.setText("" + blockObject.getStringProperty(language, StrProp.shopCostDisplay) + ": ???")
    }
    skinStatusText.setPosition(80, 83)
    skinCostText.x = 80
    skinDistanceText.x = 80
    skinIndexText.x = 80
    shopMoneyDisplay.right = shopTitle.right
}
