function manageSettings () {
    if (!(blockSettings.exists("money"))) {
        blockSettings.writeNumber("money", 0)
    }
    money = blockSettings.readNumber("money")
    if (!(blockSettings.exists("highscore"))) {
        blockSettings.writeNumber("highscore", 0)
    }
    highscore = blockSettings.readNumber("highscore")
    if (!(blockSettings.exists("deliveries"))) {
        blockSettings.writeNumber("deliveries", 0)
    }
    deliveries = blockSettings.readNumber("deliveries")
    if (!(blockSettings.exists("ownedSkins"))) {
        blockSettings.writeNumberArray("ownedSkins", [0])
    }
    ownedSkins = blockSettings.readNumberArray("ownedSkins")
    if (!(blockSettings.exists("currentSkin"))) {
        blockSettings.writeNumber("currentSkin", 0)
    }
    currentSkin = blockSettings.readNumber("currentSkin")
}
