function gameOver () {
    inGame = false
    animation.stopAnimation(animation.AnimationTypes.All, van)
    animation.stopAnimation(animation.AnimationTypes.All, timeIcon)
    lastSpeed = speed
    timer.background(function () {
        while (speed > 0) {
            speed += 0 - (2 + (lastSpeed - 60) / 140 * 1.4)
            pause(50)
        }
        if (speed < 0) {
            speed = 0
        }
        blockSettings.writeNumber("deliveries", deliveries + currentDeliveries)
        blockSettings.writeNumber("money", money + currentMoney)
        for (let index = 0; index <= skinPrices.length - 1; index++) {
            value = skinPrices[index]
            if (highscore < value[1] && distance > value[1]) {
                Notification.notify(blockObject.getStringProperty(language, StrProp.unlockedNewSkinText), 20, skinIcons[index])
            }
        }
        blockSettings.writeNumber("highscore", distance)
        resetGame()
    })
}
