    orderSprite2 = sprites.create(foodItems[currentOrders[1]][1], SpriteKind.Asset)
    orderSprite2.setPosition(30, 108)
    orderSprite3 = sprites.create(foodItems[currentOrders[2]][1], SpriteKind.Asset)
    orderSprite3.setPosition(48, 108)
