game.onUpdate(function () {
    stopMovement()
    if (van) {
        vanShadow.x = van.x
    }
    scroller.scrollBackgroundWithSpeed(positiveOrZero(speed - 80) * -1, 0, scroller.BackgroundLayer.Layer0)
    scroller.scrollBackgroundWithSpeed(positiveOrZero(speed - 40) * -1, 0, scroller.BackgroundLayer.Layer2)
    scroller.scrollBackgroundWithSpeed(speed * -1, 0, scroller.BackgroundLayer.Layer3)
    scroller.scrollBackgroundWithSpeed(speed * -1, 0, scroller.BackgroundLayer.Layer4)
    if (titleText) {
        titleText.vx = speed * -1
    }
})
game.onUpdateInterval(1000, function () {
    if (inGame) {
        if (speed + 1 <= maxVelocity) {
            speed += 1
        } else {
            speed = 170
        }
        interval = 1 / speed * 4000
        animation.runImageAnimation(
        van,
        skins[currentSkin],
        Math.round(interval),
        true
        )
        console.logValue("speed", speed)
    }
})
forever(function () {
    if (inGame) {
        if (houseSprites.length > 0) {
            if (houseSprites[houseSprites.length - 1].right <= 155) {
                spawnHouse()
            }
        } else {
            spawnHouse()
        }
    }
    for (let value of sprites.allOfKind(SpriteKind.House)) {
        value.vx = 0 - speed
    }
    for (let value of sprites.allOfKind(SpriteKind.Food)) {
        value.vx = 0 - speed
        if (value.right < 0) {
            sprites.destroy(value)
        }
    }
    if (inGame) {
        van.sayText(convertToKM(distance))
    }
})
game.onUpdateInterval(100, function () {
    if (inGame) {
        distance += Math.round(speed / 100)
    }
    if (timeBar) {
        if (timeBar.value > 0) {
            timeBar.value += (speed - 60) / -140 - 0.5
        }
    }
})
